tests.codeSolution:
const FetchLegend = ({ id }) => {

	let [legend, setLegend] = useState({ 
		firstName: '', lastName: '', error: '' 
	});

	let capitalise = str => str[0].toUpperCase() + str.slice(1);

	useEffect(async () => {
		let fetchedLegend = await fetch(`/rest/legends/${id}`)
			.then(response => response.json());
		if(fetchedLegend.name) {
			let [firstName, lastName] = fetchedLegend.name.split(' ');
			setLegend({ 
				firstName: capitalise(firstName),
				lastName: capitalise(lastName)
			});
		} else {
			setLegend({ error: 'Legend not found!' });
		}
	});

	return (
		<>
			<div className='firstName'>{legend.firstName}</div>
			<div className='lastName'>{legend.lastName}</div>
			<div className='error'>{legend.error}</div>
		</>
	);
};

