class BrexitDeal extends Component {

	constructor() {
		super();
		this.stages = { idle: 'idle', loading: 'loading', error: 'error' };
		this.state = { deal: '', error: '', stage: this.stages.idle };
	}

	getDeal() {
		let { idle, loading, error } = this.stages;
		this.setState({ stage: loading });
		fetch('/rest/brexit-deal')
			.then(response => {
				return response.json().then(body => {
					return response.ok ?
						Promise.resolve(body) :
						Promise.reject(body);	
				});
			})
			.then(deal => this.setState({ deal, stage: idle }))
			.catch(msg => this.setState({ error: msg, stage: error }));
	}

	render() {
		let { deal, stage, error } = this.state;
		return (
			<div>
				<button onClick={() => this.getDeal()}>Get deal!</button>
				<div data-message>{
					deal ?
						`Great! ${deal}` :
						stage === this.stages.loading ?
							'Loading...' :
							stage === this.stages.error ?
								`${error}, please try again in 2 months` :
								'Click on the button to proceed'
				}</div>
			</div>
		);
	}
}

