class CoolWords extends Component {

	constructor() {
		super();
		this.state = { q: '', words: [] };
	}

	async setQuery(e) {
		let q = e.target.value;
		if(q.length >= 2) {
			let url = `/rest/cool-words?q=${q}`;
			let words = await fetch(url).then(response => response.json());
			this.setState({ q, words });
		} else {
			this.setState({ q });
		}
	}

	render() {
		let { q, words } = this.state;
		return (
			<div>
				<input 
					type='text' value={q}
					onChange={e => this.setQuery(e)} />
				{
					q.length >= 2 &&
					words.map(word => (
						<div className='word' key={word}>{word}</div>
					))
				}
				<label className='results'>{
					q.length >= 2 ? 
						`${words.length} results found` :
						`Please type at least 2 characters`
				}</label>
			</div>
		);
	}
}

