it('Log reports different events of the component lifecycle', () => {

	//given
	const levels = { INFO: 'INFO', WARN: 'WARN' };
	const log = { info: sinon.stub(), warn: sinon.stub() };

	//when
	const wrapper = mount(<Log log={log} level={levels.INFO} />);

	//then
	expect(log.info).to.have.been.calledWith('mounted');
	expect(log.info).to.have.callCount(1);
	expect(log.warn).to.have.callCount(0);

	//when
	wrapper.setProps({ level: levels.WARN });

	//then
	expect(log.info)
		.to.have.been.calledWith('will update', levels.WARN, levels.INFO);
	expect(log.warn)
		.to.have.been.calledWith('did update', levels.WARN, levels.INFO);
	expect(log.info).to.have.callCount(2);
	expect(log.warn).to.have.callCount(1);

	//when
	wrapper.unmount();

	//then
	expect(log.warn).to.have.been.calledWith('will unmount', levels.WARN);
	expect(log.info).to.have.callCount(2);
	expect(log.warn).to.have.callCount(2);
});

tests.codeSolution:
class Log extends Component {

	updateEvent(event, ...args) {
		args.unshift(event);
		let level = this.props.level.toLowerCase();
		this.props.log[level].apply(null, args);
	}

	componentDidMount() {
		this.updateEvent('mounted');
	}

	componentWillUnmount() {
		this.updateEvent('will unmount', this.props.level);
	}

	componentWillUpdate(nextProps) {
		this.updateEvent('will update', nextProps.level, this.props.level);
	}

	componentDidUpdate(prevProps) {
		this.updateEvent('did update', this.props.level, prevProps.level);
	}

	render() {
		return <div></div>;
	}
}

