class DOM extends Component {

	constructor() {
		super();
		this.labelRef = React.createRef();
		this.state = { clickCount: 0 };
	}

	setAttribute() {
		let labelNode = this.labelRef.current;
		labelNode.removeAttribute(`key-${this.state.clickCount}`);
		this.setState(
			prevState => ({ clickCount: ++prevState.clickCount }),
			() => {
				let { clickCount } = this.state;
				let key = `key-${clickCount}`;
				let value = `value-${clickCount}`;
				labelNode.setAttribute(key, value);
				this.props.onClick(labelNode);
			}
		);
	}

	render() {
		return (
			<div>
				<label a='1' ref={this.labelRef}>as</label>
				<button onClick={() => this.setAttribute()}>
					Increase the counter
				</button>
			</div>
		);
	}
}

