class AddToCartButton extends Component {

	constructor() {
		super();
		this.state = { isSelected: false };
	}

	update(context) {
		this.setState(
			{ isSelected: !this.state.isSelected },
			() => {
				let message = this.state.isSelected ?
					'The item has been added to the basket' :
					'The item is not on the basket';
				context.onAddToCartClick(message);
			}
		);
	}

	render() {
		return (
			<AddToCartContext.Consumer>
				{context => (
					<div>
						<button onClick={() => this.update(context)}>{
							this.state.isSelected ? 
								'Added!' :
								'Add to the cart'
						}</button>
					</div>
				)}
			</AddToCartContext.Consumer>
		);
	}
}

