class ResultsPage extends Component {

	constructor() {
		super();
		this.state = { results: [], redirect: false };
	}

	isValidCompetition(competition) {
		return competition === 'f1' || competition === 'motogp';
	}

	isValidYear(year) {
		return Number(year) >= 1971 && Number(year) <= 2006;
	}

	componentDidMount() {
		let { competition, year } = this.props.match.params;
		if(
			!this.isValidCompetition(competition) || 
			!this.isValidYear(year)
		) {
			this.setState({ redirect: true });
		}
	}

	render() {
		return (
			<div>{
				this.state.redirect ?
					<Redirect to='/not-found' /> :
					<div>Competition and year correctly indexed</div>
			}</div>
		);
	}
}

