class Username extends React.Component {

	constructor() {
		super();
		this.state = { username: '' };
	}

	updateUsername(e) {
		this.setState({ username: e.target.value });
	}

	isValidUsername() {
		return (
			this.state.username && 
			!this.hasBeenAlreadyTaken() && 
			!this.isForbidden()
		);
	}

	hasBeenAlreadyTaken() {
		return this.props.existing
			.includes(this.state.username.toLowerCase());
	}

	isForbidden() {
		return this.props.forbidden
			.includes(this.state.username.toLowerCase());
	}

	render() {
		return (
			<div>
				<input 
					type='text' value={this.state.username}
					onChange={e => this.updateUsername(e)} />
				<label className='message'>{
					this.hasBeenAlreadyTaken() ?
						'The username is already taken' :
						this.isForbidden() ?
							'The username is not allowed' :
							this.state.username ?
								'The username is available!' :
								'Please enter your username'
				}</label>
				<input type='submit' disabled={!this.isValidUsername()}>
					Submit
				</input>
			</div>
		);
	}
}

