class AgeCheck extends React.Component {

	constructor() {
		super();
		this.state = { age: '', question: '' };
	}

	setValue(input, e) {
		this.setState({ [input]: e.target.value });
	}

	isValidAnswer() {
		let { age, question } = this.state;
		return age && age === '>=18' && question === '1989';
	}

	render() {
		return (
			<div>
				<select 
					value={this.state.age}
					onChange={e => this.setValue('age', e)}>
