				</select>{
					this.state.age === '>=18' &&
					<input 
						type='text'
						placeholder='When did the German wall fall?'
						value={this.state.question}
						onChange={e => this.setValue('question', e)} />
				}
				<label className='message'>{
					this.state.age === '<18' ?
						'Sorry, you must be 18+ to proceed' :
						this.state.question ?
							this.state.question === '1989' ?
								'Enjoy :)' :
								'Wrong answer!' :
							''
				}</label>
				<input type='submit' disabled={!this.isValidAnswer()} />
			</div>
		);
	}
}

