class Countries extends React.Component {

	constructor() {
		super();
		this.state = { filter: '' };
	}

	updateFilter(e) {
		this.setState({ filter: e.target.value });
	}

	render() {
		return (
			<div>
				<input type='text' onChange={e => this.updateFilter(e)} />
				{
					this.props.list
						.filter(country => {
							return country.includes(this.state.filter);
						})
						.map(country => <p key={country}>{country}</p>)
				}
			</div>
		);
	}
}

